INSERT INTO `t_vr` VALUES ('13', 'DY013', 'http://vr.hunli720.com/member/preview/view/pano_id/17', 'DY013.png', '缘定今生', '4', '本场婚礼用“圆形”象征爱情中的缘分，取“圆圆满满”的美好寓意。本场的亮点是外场的座次表和展示区融为一体，用巨大的圆形铁艺做为主结构的支撑，在圆形的铁艺中间粘贴座次表，座次表的形式是八个圆形背板做自由排列，圆形铁艺上装饰放射状红色花簇。内场的舞台两侧摆放LED屏幕，播放新人的婚纱照和影像资料，中间处摆放红色背景板。', '&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&lt;font face=&quot;宋体&quot;&gt;签到区：大型双层铁艺圆环&lt;/font&gt;1&lt;font face=&quot;宋体&quot;&gt;组&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;花簇&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;3&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;组&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;圆形背景板&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;8&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;个&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;签到用品&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;1&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;套。&lt;/font&gt;&lt;/span&gt;&lt;span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&lt;font face=&quot;宋体&quot;&gt;舞台区：高精&lt;/font&gt;LED&lt;font face=&quot;宋体&quot;&gt;屏幕尺寸根据实际而定&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;画面背板&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;2&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;组。 &amp;nbsp;&lt;/font&gt;&lt;/span&gt;&lt;span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&lt;font face=&quot;宋体&quot;&gt;细节区：椅背装饰若干。&lt;/font&gt; &lt;/span&gt;&lt;span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;