INSERT INTO `t_vr` VALUES ('65', 'QX004', 'http://vr.hunli720.com/member/preview/view/pano_id/70', 'QX004.png', '晴光潋滟', '2', '纷至沓来的情愫，伴随琴弦飞舞，细水流长般旋律缓缓从指间流露出来，鸣响在时光深处，清晰了彼此的面容，一双人，一首曲，指若相扣，留住了年华。时间掉在水里，被蓝色缓缓盖住，万物乘风生长，吸饱夏日的阳光静静存在，时光伫立变成永恒。室外婚礼最美的地方在于有蓝天白云做为见证。仪式区做镂空式背景，以金属材质的铁艺框架，装饰花簇，其他不做遮挡，让阳光洒在新人的脸颊。', '&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&lt;font face=&quot;宋体&quot;&gt;仪式区：铁艺镂空框架&lt;/font&gt;8&lt;font face=&quot;宋体&quot;&gt;组&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;花簇&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;5&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;组&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;花排若干&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;地毯&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;1&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;组&lt;/font&gt;&lt;font face=&quot;Calibri&quot;&gt;+&lt;/font&gt;&lt;font face=&quot;宋体&quot;&gt;气球若干。&lt;/font&gt;&lt;/span&gt;&lt;span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span&gt;细节区：椅背装饰若干。&lt;/span&gt;&lt;span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;', null, '1782', '4', '0', '88888', '1545979090', '0');
